INSERT [dbo].[OrganizationDescriptions] ([Id], [Uid], [OrganizationId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (27, N'533efa18-2b99-4bda-9d71-fa0d57338e15', 14, 1, N'<p style="margin-bottom:.0001pt"><span style="line-height:normal"><span style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,&quot;sans-serif&quot;"><span style="color:black">The Arte 1 channel offers 24 hour of content totally dedicated to art. We have four weekly own productions: Arte 1 Em Movimento, Estilo Arte 1, Arte 1 Comtexto and Magazine com The New York Times. </span></span></span></span></span></p>

