INSERT [dbo].[OrganizationDescriptions] ([Id], [Uid], [OrganizationId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (68, N'4c62b4cb-0132-490c-984a-9954244e1eda', 34, 2, N'<p>Caracol Televisi&oacute;n S.A. &eacute; uma empresa colombiana de m&iacute;dia e entretenimento que produz e transmite conte&uacute;do multiplataforma que &eacute; exportado e distribu&iacute;do atrav&eacute;s de seus canais locais, internacionais e de terceiros. Comercializa diferentes m&iacute;dias, como jornais, revistas, r&aacute;dio e m&iacute;dia eletr&ocirc;nica; presta servi&ccedil;os de produ&ccedil;&atilde;o a terceiros utilizando sua capacidade instalada e opera o canal nacional de televis&atilde;o aberta com o mesmo nome (Caracol Televisi&oacute;n). Com escrit&oacute;rios em Miami, Madrid e Bogot&aacute;, Caracol distribui conte&uacute;do (telenovelas, s&eacute;ries e formatos de entretenimento) a n&iacute;vel mundial, atrav&eacute;s de todas as barreiras culturais e lingu&iacute;sticas, e ganhando reconhecimento mundial por seus resultados em mercados onde os mesmos foram emitidos ou produzidos. Com mais de 45 anos de experi&ecirc;ncia no setor de televis&atilde;o, a Caracol produz mais de 6.000 horas de material audiovisual por ano e possui mais de 12.000 horas de livraria original. Seu conte&uacute;do foi transmitido em quase 80 pa&iacute;ses nos cinco continentes e as adapta&ccedil;&otilde;es locais de seus roteiros se tornaram um sucesso internacional. Hoje, Caracol Televisi&oacute;n &eacute; o segundo distribuidor global de conte&uacute;do em espanhol</p>
', 0, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1)
