INSERT [dbo].[OrganizationDescriptions] ([Id], [Uid], [OrganizationId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (180, N'a282e7a5-4f69-46fe-868a-65442f138092', 90, 2, N'<p>Desde 1998, a Globo Filmes j&aacute; participou de mais de 200 filmes, levando ao p&uacute;blico o que h&aacute; de melhor no cinema brasileiro. Com a miss&atilde;o de contribuir para o fortalecimento da ind&uacute;stria audiovisual nacional, a filmografia contempla v&aacute;rios g&ecirc;neros, como com&eacute;dias, infantis, romances, document&aacute;rios, dramas e aventuras, apostando na diversidade e em obras que valorizam a cultura brasileira. A Globo Filmes participou de alguns dos maiores sucessos de p&uacute;blico e de cr&iacute;tica como, &lsquo;Tropa de Elite 2&rsquo;, &lsquo;Minha M&atilde;e &eacute; uma Pe&ccedil;a 2&rsquo; &ndash; com mais de 9 milh&otilde;es de espectadores -, &lsquo;Se Eu Fosse Voc&ecirc; 2&rsquo;, &lsquo;2 Filhos de Francisco&rsquo;, &lsquo;Aquarius&rsquo;, &lsquo;Que Horas Ela Volta?&rsquo;, &lsquo;O Palha&ccedil;o&rsquo;, &lsquo;Get&uacute;lio&rsquo;, &lsquo;Carandiru&rsquo; e &lsquo;Cidade de Deus&rsquo; &ndash; com quatro indica&ccedil;&otilde;es ao Oscar. Suas atividades se baseiam em uma associa&ccedil;&atilde;o de excel&ecirc;ncia com produtores independentes e distribuidores nacionais e internacionais.</p>
