INSERT [dbo].[OrganizationDescriptions] ([Id], [Uid], [OrganizationId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (288, N'0255b910-fbea-454d-beb1-72698a85ec0e', 144, 2, N'<p>ORF &eacute; a empresa de meios independentes da &Aacute;ustria com um mandato de lei p&uacute;blico, ganhando uma metade da renda das taxas do programa e obtendo o restante atrav&eacute;s de propaganda. ORF oferece uma ampla gama de informa&ccedil;&otilde;es, educa&ccedil;&atilde;o, cultura, ci&ecirc;ncia, esportes e entretenimento com filmes e s&eacute;ries. Os conte&uacute;dos s&atilde;o fornecidos atrav&eacute;s de 3 canais de TV e 14 canais de r&aacute;dio e Internet.</p>
', 0, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1)
