INSERT [dbo].[OrganizationDescriptions] ([Id], [Uid], [OrganizationId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (413, N'02c462d7-eebd-46e4-a09b-6ab152116b15', 207, 1, N'<p>PARAMOUNT CHANNEL is a 24-hour, ad-supported television network featuring the movies of Paramount Pictures, America&#39;s oldest motion picture studio and one of the world&#39;s leading producers of filmed entertainment. PARAMOUNT CHANNEL creates a contemporary and unique environment for audiences to celebrate film, documentaries and behind-the-scenes features. Viewers are given access to Paramount Pictures&#39; vast library of beloved, award-winning films — from visionary epics to heart-wrenching romances to blockbuster franchises, all created by the best talent and filmmakers in the business. PARAMOUNT CHANNEL is part of Viacom International Media Networks (VIMN), a division of Viacom Inc. (NASDAQ: VIA, VIAB).</p>
', 0, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1)
