INSERT [dbo].[OrganizationDescriptions] ([Id], [Uid], [OrganizationId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (418, N'2f4b505f-0d54-4f3e-be57-45347d5e1966', 209, 2, N'<p>A Telefe &eacute; l&iacute;der na produ&ccedil;&atilde;o, distribui&ccedil;&atilde;o e venda de servi&ccedil;os de conte&uacute;do e multiplataforma na Argentina. Ele det&eacute;m nove licen&ccedil;as de TV broadcast e dois canais; um nacional e um internacional com mais de 10 milh&otilde;es assinantes. Por 26 anos, a Telefe manteve sua posi&ccedil;&atilde;o de lideran&ccedil;a com conte&uacute;do de alta qualidade. Com mais de 12.000 m2 de espa&ccedil;o de produ&ccedil;&atilde;o, produz o seu pr&oacute;prio conte&uacute;do, bem como produz para terceiros, tanto a n&iacute;vel nacional como internacional. Est&aacute; presente em todos os continentes como um dos mais reconhecidos produtores de conte&uacute;do de l&iacute;ngua espanhola, atualmente distribu&iacute;do em mais de 100 pa&iacute;ses.</p>
', 0, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.463' AS DateTime), 1)
