INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (1, N'b0e11fdd-8e7b-45b8-9b40-56d76ee253a4', 1, 1, N'<p style="margin-right:25.8pt; text-align:justify"><span lang="EN-US" style="font-size:11.0pt"><span style="font-family:&quot;Calibri&quot;,sans-serif">2DLab is an award winning animation studio and production company from located in Rio de Janeiro, Brazil. Over the last 8 years we have produced and animated more than 50 hours of animation. We produce and animate Television series, Theatrical Films, shorts and interstitials. Our most successful franchise has been the preschool TV series &ldquo;My Big Big Friend&rdquo; produced for Discovery Kids and Treehouse TV (Canada). The series in its 3<sup>rd</sup> season and has been sold to over 40 territories. A Theatrical film will be released in 2017.</span></span></p>

<p style="margin-right:25.8pt; text-align:justify"><span lang="EN-US" style="font-size:11.0pt"><span style="font-family:&quot;Calibri&quot;,sans-serif">2DLab has coproduced very successfully with Breakthrough Entertainment (Canada) and others. We are interested in co-productions and distribution deals with the US for animated TV series and Theatrical Films. One of our next big projects will be an animated adult film based on one of Brazil&rsquo;s most important movies. (winner of the palm D&rsquo;Or). The film will have an American director. We will also be starting a few new series in 2017, as well as animating the new season of &ldquo;My Big Big Friend&rdquo;. For more information, please contact Andre Breitman at andre@2dlab.com</span></span></p>
', 0, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1)
GO
INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (2, N'd2f505d4-cada-4181-ac35-b6be974b0bbb', 1, 2, N'<p style="margin:0cm; margin-bottom:.0001pt">Juntos, Andr&eacute; Breitman e Andres Lieban j&aacute; desenvolveram v&aacute;rias s&eacute;ries para televis&atilde;o. Destas, quatro s&eacute;ries est&atilde;o em produ&ccedil;&atilde;o: <i>Mitos do Mondo (TVC)</i>, <i>Minuto do Mondo (TVC), Meu Amig&atilde;oz&atilde;o (Treehouse- Canad&aacute;), Quarto do Jobi (TV Brasil)</i>. Duas s&eacute;ries ser&atilde;o feitas atrav&eacute;s de co-produ&ccedil;&otilde;es internacionais. A primeira- My Big Big Friend (Amig&atilde;oz&atilde;o)- com a Breakthrough Entertainment (Toronto) e a segunda &ndash; NEO &ndash; com a Galakids (Montreal).</p>

<p style="margin:0cm; margin-bottom:.0001pt">Com o nome mais internacional de 2DLab o Laborat&oacute;rio de Desenhos j&aacute; ganhou, com sua s&eacute;rie My Big Big Friend, o &ldquo;<i>Global Licensing Award</i> &ldquo; na Mipcom Jr. de 2007.</p>

<p style="margin:0cm; margin-bottom:.0001pt">&nbsp;</p>

<p style="margin:0cm; margin-bottom:.0001pt"><b><u>AMIGAOZ&Atilde;O</u></b></p>

<p style="margin:0cm; margin-bottom:.0001pt">A s&eacute;rie Meu AmigaoZ&atilde;o, agora na sua segunda temporada, &eacute; uma das maiores co-produ&ccedil;&otilde;es audiovisuais j&aacute; feitas no Brasil. Seus 104 epis&oacute;dios s&atilde;o nada menos do que 1,1150 minutos &ndash; 19 horas de anima&ccedil;&atilde;o ou seja- quase 13 longa metragens, feitos em 4 anos. A equipe de Storyboard e anima&ccedil;&atilde;o &eacute; totalmente Brasileira e a maioria dos seus 110 animadores foram treinados dentro do CRIA- a escola de Anima&ccedil;&atilde;o interna do pr&oacute;prio est&uacute;dio.</p>
', 0, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1)
GO
