INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (19, N'230009dd-6a8b-45cf-be61-f8ef88291d71', 11, 1, N'<p>ARTE is a European cultural broadcaster.&nbsp;As the European Culture Channel, ARTE provides cultural programming that fosters understanding among Europeans and brings people together. Since its creation in 1991, ARTE stands for cultural diversity and multilingualism. ARTE uncovers new talent, encourages artistic creativity, and preserves and promotes Europe&rsquo;s cultural heritage.&nbsp;ARTE supports talented film-makers as a co-producer and shows ambitious new creations, daring drama and series. On offer are also bold documentaries, no infotainment but daily news of vital interest to Europeans. Not to forget the live music and performances and avant-garde magazines.</p>
', 0, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1)
