INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (21, N'e4ada1ae-aed0-4e03-849f-4e75a684f825', 12, 1, N'<p style="margin:0cm; margin-bottom:.0001pt"><span lang="EN-US" style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">The Arte 1 channel offers 24 hour of content totally dedicated to art. We have four weekly own productions: Arte 1 Em Movimento, Estilo Arte 1, Arte 1 Comtexto and Magazine com The New York Times. </span></span></span></span></p>

<p style="margin:0cm; margin-bottom:.0001pt"><span lang="EN-US" style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Besides our own productions, Arte1 has an exclusive slot dedicated to national movies and documentaries, which has already aired titles as &ldquo;Marina Abramovic In Brazil: The Space In Between&rdquo; and &ldquo;Sudoeste&rdquo;. Movies claimed by the worldwide critics like &ldquo;Breathless&rdquo;, directed by Jean Luc Godard and &ldquo;Raging Bull&rdquo;, directed by Martin Scorsese, are also presented in two especial slots: Cine Clube Arte1 and Cine Clube Arte1 Cl&aacute;ssicos. The channel has weekly sessions dedicated to international pop-music shows, classical music concerts and dance performances. </span></span></span></span></p>

<p style="margin:0cm; margin-bottom:.0001pt"><span lang="EN-US" style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Arte 1 also invests in co-productions and bets on documentaries series that promote national independent production and explore the universe of art as &quot;Geografia da Arte&quot;, &quot;Work in Progress&quot;, &quot;Inven&ccedil;&otilde;es da Alma&quot; and &quot;Artistas Pl&aacute;sticos Brasileiros&quot;.</span></span></span></span></p>

<p style="margin:0cm; margin-bottom:.0001pt"><span lang="EN-US" style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">After a premiere in December 2012 on Sky TV and Claro TV, Arte 1 was officially launched in March 2013 by Bandeirantes Comunication Group. Currently, is available in almost all main pay-tv operators and independent ones, which corresponds to over than 12 million subscribers throughout Brazil.</span></span></span></span></p>
', 0, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1)
GO
INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (22, N'c44ea788-8118-42d3-bc33-9e129d10a982', 12, 2, N'<p style="margin:0cm; margin-bottom:.0001pt"><span style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">O canal Arte 1 oferece 24 horas de conte&uacute;do totalmente dedicado &agrave; arte. Temos quatro produ&ccedil;&otilde;es pr&oacute;prias semanais: Arte 1 Em Movimento, Estilo Arte 1, Arte 1 Comtexto e Magazine com The New York Times.</span></span></span></span></p>

<p style="margin:0cm; margin-bottom:.0001pt"><span style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Al&eacute;m das produ&ccedil;&otilde;es pr&oacute;prias, o Arte1 possui uma faixa dedicada a filmes e document&aacute;rios nacionais, que j&aacute; exibiu t&iacute;tulos como &quot;Espa&ccedil;o Al&eacute;m - Marina Abramovic e o Brasil&quot; e &quot;Sudoeste&quot;. T&iacute;tulos aclamados pelas cr&iacute;ticas mundiais como &quot;Acossado&quot;, dirigido por Jean Luc Godard e &quot;Touro Indom&aacute;vel&quot;, dirigido por Martin Scorsese, tamb&eacute;m s&atilde;o apresentados em dois slots especiais: Cine Clube Arte1 e Cine Clube Arte1 Cl&aacute;ssicos. O canal apresenta sess&otilde;es semanais especialmente dedicadas a shows internacionais de m&uacute;sica pop, concertos de m&uacute;sica cl&aacute;ssica e performances de dan&ccedil;a.</span></span></span></span></p>

<p style="margin:0cm; margin-bottom:.0001pt"><span style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">O Arte 1 tamb&eacute;m investe em coprodu&ccedil;&otilde;es e aposta em s&eacute;ries de document&aacute;rios que promovem a produ&ccedil;&atilde;o independente nacional e exploram o universo da arte como &quot;Geografia da Arte&quot;, &quot;Work in Progress&quot;, &quot;Inven&ccedil;&otilde;es da Alma&quot; e &quot;Artistas Pl&aacute;sticos Brasileiros&quot;.</span></span></span></span></p>

<p style="margin:0cm; margin-bottom:.0001pt"><span style="font-size:13.5pt"><span style="background:white"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Ap&oacute;s um exibi&ccedil;&atilde;o em car&aacute;ter experimental em dezembro de 2012 na Sky TV e Claro TV, o Arte 1 foi lan&ccedil;ado oficialmente em mar&ccedil;o de 2013 pelo Grupo de Comunica&ccedil;&atilde;o Bandeirantes. Atualmente, est&aacute; dispon&iacute;vel em quase todos os principais operadores de televis&atilde;o por assinatura e independentes do Brasil, o que corresponde a mais de 12 milh&otilde;es de assinantes em todo o pa&iacute;s.</span></span></span></span></p>
', 0, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1)
GO
