INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (171, N'3a975fb0-fedb-4c56-8e33-bafd47a044de', 87, 1, N'<p>Established in 2001, Moonshot is a Film &amp; TV Brazilian production company creating, developing and producing content-based projects built in a strong storytelling team.</p>

<p>Some of our TV series produced: &ldquo;9MM S&atilde;o Paulo&rdquo; (Fox Latin American Channels), &ldquo;Sess&atilde;o de Terapia&rdquo; (GNT), &ldquo;Santo Forte&rdquo; (Canal AXN) and &ldquo;171 &ndash; Neg&oacute;cio de Fam&iacute;lia&rdquo; (Universal Channel).</p>

