INSERT [dbo].[HoldingDescriptions] ([Id], [Uid], [HoldingId], [LanguageId], [Value], [IsDeleted], [CreateDate], [CreateUserId], [UpdateDate], [UpdateUserId]) VALUES (214, N'229b8275-8fa2-4332-ae8f-4f5ad0955364', 108, 2, N'<p>Rede Record &eacute; uma rede de televis&atilde;o aberta brasileira fundada por Paulo Machado de Carvalho, em 27 de setembro de 1953, sendo a mais antiga emissora de televis&atilde;o em atividade no pa&iacute;s. No final da d&eacute;cada de 1980, o canal, que antes pertencia a Paulo Machado de Carvalho e Silvio Santos, foi comprado pelo empres&aacute;rio e l&iacute;der religioso Edir Macedo, fundador da Igreja Universal do Reino de Deus.As emissoras co-geradoras da Rede Record s&atilde;o a TV Record S&atilde;o Paulo e a TV Record Rio de Janeiro. A partir de 2007, consolidou-se como a segunda maior rede de TV no Brasil em audi&ecirc;ncia e faturamento, posi&ccedil;&atilde;o que passou a ser disputada mais intensamente com o SBT a partir de 2012, com ambas disputando d&eacute;cimos pontuais e revezando-se no ranking do Ibope. Hoje, &eacute; a 28.&ordf; maior emissora de televis&atilde;o comercial do mundo. Sua sede est&aacute; localizada no bairro da Barra Funda, na cidade de S&atilde;o Paulo.</p>
', 0, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1, CAST(N'2019-08-09 13:30:17.423' AS DateTime), 1)
